package projeto1;

public class OlaMundo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Olá Mundo");
		System.out.println("Hello World");
		System.out.println("Olá Mundinho");
		String amigo = "Ximporimfola";
		System.out.println("Olá " + amigo + " Mundão");

		int idade; // Declara uma variável
		idade = 20; // Atribui valor à variável
		double altura = 1.75; // Declara e atribui valor à variável simultaneamente
		char letra = 'A';
		boolean alunoAtivo = true;
		String nome = "Carlos Alexandre";

		System.out.println("Nome: " + nome);
		System.out.println("Idade: " + idade);
		System.out.println("Caracter: " + letra);
		System.out.println("Altura: " + altura);
		System.out.println("É Aluno: " + alunoAtivo);
	}

}
